//Login component
import React, { useState } from "react";
import axios from 'axios';

//react bootstrap imports
import Form from 'react-bootstrap/Form';
import Button from "react-bootstrap/Button";
import '../styles/Login.css';

import { Link } from "react-router-dom";

function Login(){
    //states
    const [loginUsername, setLoginUsername] = useState("");
    const [loginPassword, setLoginPassword] = useState("");

    const login = (e) =>{
        e.preventDefault();

        const user = {
            loginUsername,
            loginPassword
        }

        axios.post("http://localhost:8070/user/login", user).then((res) => {
            alert("Login successful!")
            console.log(res);
            console.log(user);
        }).catch((err) => {
            console.log(err)
        })
    }

    return (
        <div className="loginBody">
            <div className="loginBlock">

                <h1>Log in</h1>

                <Form onClick={login}>
                    <Form.Group className="mb-3" controlId="formUsername">
                        <Form.Label>Username</Form.Label>
                        <Form.Control type="text" placeholder="Enter email" className='formText'
                        onChange = {(e) => {
                            setLoginUsername(e.target.value);
                        }}/>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" className='formText'
                        onChange = {(e) => {
                            setLoginPassword(e.target.value);
                        }}/>
                    </Form.Group>
        
                    <div className='flex-container'>
                        <Button variant="primary" type="submit" className='loginBtn'>
                            Log In
                        </Button>
                    </div>

                    <div className='flex-container'>
                        <Link to="/createAccountPage">
                            <li>Register</li>
                        </Link>
                    </div>
                
                </Form>
            </div>
        </div>
    );
}

export default Login;
