//Create account component
import React, {useState} from "react";

//bootstrap imports
import Form from 'react-bootstrap/Form';
import Button from "react-bootstrap/Button";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
//custom styles
import '../styles/CreateAccount.css';

function CreateAccount(){
    //set states
    const [inputField, setInputField] = useState({
        username: '',
        password: '',
        reEnterPassword: '',
        accType: '',
        accLevel: ''
    })

    //handle change
    function handleChange(e){
        setInputField( {[e.target.name]: e.target.value} )
    }

    //handle submit
    function handleSubmit(e){
        e.preventDefault();
        let username = this.state.username;
        let password = this.state.password;
        let reEnterPassword = this.state.reEnterPassword;
        let accType = this.state.accType;
        let accLevel = this.state.accLevel;


        if (password !== reEnterPassword){
            alert("Passwords don't match!");
        }
        else{
            alert("good to go!" + password + " " + reEnterPassword);

            const newUser = {
                username,
                password,
                accType,
                accLevel
            }

            console.log(newUser);
        }
    }

    return (
        <div>
            <div className='createAccountBlock'>
                <h1>Create Account</h1>

                <form onSubmit={handleSubmit}>
                    <Form.Group name="username" className="mb-3" controlId="username">
                        <Form.Label>Username</Form.Label>
                        <Form.Control type="text" placeholder="Enter Username" className='formText'
                        onChange = {handleChange}
                        />
                    </Form.Group>

                    <Form.Group name="password" className="mb-3" controlId="password">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" className='formText'
                        onChange = {handleChange}
                        />
                    </Form.Group>

                    <Form.Group name="reEnterPassword" className="mb-3" controlId="reEnterPassword">
                        <Form.Label>Re-enter Password</Form.Label>
                        <Form.Control type="password" placeholder="Re-enter Password" className='formText'
                        onChange = {handleChange}
                        />
                    </Form.Group>

                    <Container>
                        <Row>
                            <Col>
                                <Form.Select name="accType" aria-label="Default select example" onChange = {handleChange}>
                                    <option value="empty">Select Account Type</option>
                                    <option value="customer">Customer</option>
                                    <option value="instructor">Instructor</option>
                                </Form.Select>
                            </Col>
                            <Col>
                                <Form.Select name="accLevel" aria-label="Default select example" onChange = {handleChange}>
                                    <option>Select Account Level</option>
                                    <option value="1">Level 1 (user)</option>
                                    <option value="2">Level 2 (admin)</option>
                                </Form.Select>
                            </Col>
                        </Row>

                        <div className='flex-container'>
                            <Button variant="primary" type="submit" className='btn1'>
                                Create Account
                            </Button>
                        </div>
                    </Container>
                </form>
            </div>
        </div>
    );
}

export default CreateAccount;
